      "change_hash": "807c9f9e779c2908ce649ddd4663e670",
      "url": "http:\/\/legiscan.com\/HI\/bill\/HB738\/2012",
      "status_date": "2011-01-24",
      "status": "1",
      "last_action_date": "2011-12-01",
      "last_action": "Carried over to 2012 Regular Session.",
