      "number": "SB718",
      "change_hash": "3a716cf36afde4466424334c186af5ad",
      "url": "http:\/\/legiscan.com\/HI\/bill\/SB718\/2012",
      "status_date": "2011-01-21",
      "status": "1",
      "last_action_date": "2011-12-01",
      "last_action": "Carried over to 2012 Regular Session.",
