      "number": "SB221",
      "change_hash": "037717533d8c73a94bca86b674a96343",
      "url": "http:\/\/legiscan.com\/HI\/bill\/SB221\/2013",
      "status_date": "2013-01-18",
      "status": "1",
      "last_action_date": "2013-01-22",
      "last_action": "Referred to EGH, JDL.",
