      "change_hash": "86d3c8ae52e9e076633a2ffeb360f743",
      "url": "http:\/\/legiscan.com\/HI\/bill\/HB190\/2014",
      "status_date": "2013-03-05",
      "status": "2",
      "last_action_date": "2013-12-18",
      "last_action": "Carried over to 2014 Regular Session.",
