      "change_hash": "37e57da65f85818f44d79ec884f65964",
      "url": "http:\/\/legiscan.com\/HI\/bill\/HB203\/2014",
      "status_date": "2013-01-18",
      "status": "1",
      "last_action_date": "2013-12-18",
      "last_action": "Carried over to 2014 Regular Session.",
